int filecmp(char *path1, char *path2)
{
	int fd1 = open(path1, O_RDONLY);
	int fd2 = open(path2, O_RDONLY);
	char l1 = '1';
	char l2 = '2';
	while (l1 && l2)
	{
		if (read(fd1, &l1, 1) != read(fd2, &l2, 1) || l1 != l2)
		{
			close(fd1);
			close(fd2);
			return (l1 - l2);
		}
	}
	close(fd1);
	close(fd2);
	return (0);
}

int write_test(char *path1, char *path2, char *text, size_t size, int fdtest)
{
	int count_asm;
	int count_orig;
	int fd_asm;
	int fd_orig;
	int errnoasm;
	int errnoorig;
	int res;

	errnoasm = 0;
	errnoorig = 0;
	if (!fdtest)
		fd_asm = open(path1, O_WRONLY | O_APPEND | O_TRUNC, 0666);
	count_asm = ft_write(fdtest ? -1 : fd_asm, text, size);
	errnoasm = errno;
	if (!fdtest)
		close(fd_asm);
	if (!fdtest)
		fd_orig = open(path2, O_WRONLY | O_APPEND | O_TRUNC, 0666);
	count_orig = write(fdtest ? -1 : fd_orig, text, size);
	errnoorig = errno;
	if (!fdtest)
		close(fd_orig);
	//res = filecmp(path1, path2) == 0 && count_asm == count_orig && errnoasm == errnoorig;
	res = count_asm == count_orig && errnoasm == errnoorig;
	if (res)
		printf("****************\tWRITE TEST BELOW IS PASSED!\t****************\n");
	else
		printf("****************\tWRITE TEST BELOW NOT PASSED!!!!!!!!!!!!\t****************\n");
	printf("write asm = %d\n"
		 "write orig = %d\nerrno asm = %d\nerrno orig = %d\n****************************************************************************************\n\n\n", count_asm, count_orig, errnoasm, errnoorig);
	return (res);
}


