int ft_strcmp_test(char *s1, char *s2)
{
	int r_asm;
	int r_orig;

	r_asm = ft_strcmp(s1, s2);
	r_orig = strcmp(s1, s2);
	int res = r_asm == r_orig;
	if (res)
		printf("****************\tSTRCMP TEST BELOW IS PASSED!\t****************\n");
	else
		printf("****************\tSTRCMP TEST BELOW NOT PASSED!!!!!!!!!!!!\t****************\n");
	printf("asm strcmp = %d\n"
		 "orig strcmp = %d\nstr1 = %s\nstr2 = %s\n****************************************************************************************\n\n\n", r_asm, r_orig, s1, s2);
	return (r_asm == r_orig);
}

