int ft_strcpy_test(char *src, int allocation_size)
{
	int res;
	char *dst_asm = malloc(sizeof(char) *(allocation_size + 1));
	char *dst_orig = malloc(sizeof(char) *(allocation_size + 1));
	char *as = ft_strcpy(dst_asm, src);
	char *orig = strcpy(dst_orig, src);
	res = !strcmp(as, orig);
	if (res)
		printf("****************\tSTRCPY TEST BELOW IS PASSED!\t****************\n");
	else
		printf("****************\tSTRCPY TEST BELOW NOT PASSED!!!!!!!!!!!!\t****************\n");
	printf("dst_asm = %s\nlen_asm = %lu\ndst_orig = %s\nlen_orig = %lu\n****************************************************************************************\n\n\n", as, strlen(as), orig, strlen(orig));
	free(dst_asm);
	free(dst_orig);
	return (res);
}

