int ft_strdup_test(char *s)
{
	int res;
	char *dst_asm = ft_strdup(s);
	int errno_asm = errno;
	char *dst_orig = strdup(s);
	int errno_orig = errno;
	if ((res = !strcmp(dst_asm, dst_orig)))
		printf("****************\tSTRDUP TEST BELOW IS PASSED!\t****************\n");
	else
		printf("****************\tSTRDUP TEST BELOW NOT PASSED!!!!!!!!!!!!\t****************\n");
	printf("dst_asm = %s\ndst_orig = %s\nlen_asm = %lu\nlen_orig = %lu\nerrno_asm = %d\nerrno_orig = %d\n****************************************************************************************\n\n\n",
	dst_asm, dst_orig, strlen(dst_asm), strlen(dst_orig),errno_asm, errno_orig);
	free(dst_asm);
	free(dst_orig);
	return (res);
}

